import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [message, setMessage] = useState('');
  const [chatHistory, setChatHistory] = useState([]);
  const [userId] = useState('user123'); // Replace with dynamic user ID if needed
  const [ticketId, setTicketId] = useState('');

  useEffect(() => {
    // Fetch initial chat history if needed
  }, []);

  const handleSend = async () => {
    if (!message.trim()) return;

    const response = await axios.post('http://localhost:5000/chat', {
      message,
      user_id: userId,
    });
    setChatHistory((prev) => [...prev, { user: message, bot: response.data.message }]);
    setMessage('');
  };

  const handleReset = async () => {
    await axios.post('http://localhost:5000/reset');
    setChatHistory([]);
  };

  const handleCheckStatus = async () => {
    if (!ticketId) return;
    const response = await axios.post('http://localhost:5000/chat', {
      message: `check_status ${ticketId}`,
      user_id: userId,
    });
    setChatHistory((prev) => [...prev, { user: `check_status ${ticketId}`, bot: response.data.message }]);
  };

  return (
    <div className="chat-container">
      <h1 className="chat-header">Agentic Chat</h1>
      <div className="chat-box">
        {chatHistory.map((chat, index) => (
          <div key={index} className={`message ${chat.user === `check_status ${ticketId}` ? 'user' : chat.user ? 'user' : 'bot'}`}>
            <span>{chat.user === `check_status ${ticketId}` ? 'You' : chat.user ? 'You' : 'Bot'}:</span>
            <div>{chat.user === `check_status ${ticketId}` ? `Check status for ticket ${ticketId}` : chat.user || chat.bot}</div>
          </div>
        ))}
      </div>
      <input
        type="text"
        value={message}
        onChange={(e) => setMessage(e.target.value)}
        placeholder="Type your message..."
      />
      <button className="send" onClick={handleSend}>
        Send
      </button>
      <input
        type="text"
        value={ticketId}
        onChange={(e) => setTicketId(e.target.value)}
        placeholder="Enter Ticket ID to check status..."
      />
      <button className="check-status" onClick={handleCheckStatus}>
        Check Status
      </button>
      <button className="reset" onClick={handleReset}>
        Reset Chat
      </button>
    </div>
  );
}

export default App;
