import { useState, useEffect, useRef } from 'react';
import { Send, RotateCcw, Bot, User } from 'lucide-react';

export default function ChatBot() {
  const [message, setMessage] = useState('');
  const [chatHistory, setChatHistory] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [userId] = useState('user_' + Math.random().toString(36).substring(2, 9));
  const messagesEndRef = useRef(null);
  const inputRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [chatHistory]);

  const handleSubmit = async (e) => {
    if (e) e.preventDefault();
    if (!message.trim()) return;

    // Add user message to chat history
    const userMessage = { type: 'user', content: message };
    setChatHistory(prev => [...prev, userMessage]);
    setIsLoading(true);
    setMessage('');

    try {
      const response = await fetch('/chat', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          message: message,
          user_id: userId
        }),
      });

      const data = await response.json();

      // Add bot response to chat history
      setChatHistory(prev => [...prev, { type: 'bot', content: data.message }]);
    } catch (error) {
      console.error('Error:', error);
      setChatHistory(prev => [...prev, { 
        type: 'bot', 
        content: 'Sorry, something went wrong. Please try again.' 
      }]);
    } finally {
      setIsLoading(false);
      inputRef.current?.focus();
    }
  };

  const resetChat = async () => {
    setIsLoading(true);
    try {
      await fetch('/reset', {
        method: 'POST',
      });
      setChatHistory([]);
    } catch (error) {
      console.error('Error resetting chat:', error);
    } finally {
      setIsLoading(false);
      inputRef.current?.focus();
    }
  };

  const handleKeyDown = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSubmit();
    }
  };

  return (
    <div className="flex flex-col h-screen bg-gray-50">
      {/* Header */}
      <header className="bg-blue-600 text-white p-4 shadow-md">
        <div className="container mx-auto flex justify-between items-center">
          <div className="flex items-center">
            <Bot className="mr-2" size={28} />
            <h1 className="text-xl font-bold">AI Support Assistant</h1>
          </div>
          <button 
            onClick={resetChat}
            className="bg-blue-500 hover:bg-blue-700 text-white py-2 px-4 rounded flex items-center transition-colors duration-300"
            disabled={isLoading}
          >
            <RotateCcw size={16} className="mr-2" />
            Reset Chat
          </button>
        </div>
      </header>

      {/* Chat Container */}
      <div className="flex-1 overflow-y-auto p-4 container mx-auto max-w-4xl">
        {chatHistory.length === 0 ? (
          <div className="flex flex-col items-center justify-center h-full text-gray-500">
            <Bot size={64} className="mb-4 text-blue-500" />
            <p className="text-lg">Welcome! How can I assist you today?</p>
            <p className="text-sm mt-2">Ask about services or check the status of your ticket.</p>
          </div>
        ) : (
          <div className="space-y-4">
            {chatHistory.map((msg, index) => (
              <div 
                key={index} 
                className={`flex ${msg.type === 'user' ? 'justify-end' : 'justify-start'}`}
              >
                <div 
                  className={`flex items-start max-w-3xl ${
                    msg.type === 'user' 
                      ? 'bg-blue-600 text-white rounded-t-lg rounded-bl-lg' 
                      : 'bg-white border border-gray-200 rounded-t-lg rounded-br-lg shadow-sm'
                  } p-4`}
                >
                  <div className={`${msg.type === 'user' ? 'order-2 ml-3' : 'order-2 ml-3'}`}>
                    {msg.type === 'user' ? (
                      <User size={20} className="text-white" />
                    ) : (
                      <Bot size={20} className="text-blue-600" />
                    )}
                  </div>
                  <div className={`${msg.type === 'user' ? 'order-1 mr-3' : 'order-1 mr-3'}`}>
                    <p className={`${msg.type === 'user' ? 'text-white' : 'text-gray-800'}`}>
                      {msg.content}
                    </p>
                  </div>
                </div>
              </div>
            ))}
            <div ref={messagesEndRef} />
          </div>
        )}
      </div>

      {/* Loading indicator */}
      {isLoading && (
        <div className="flex justify-center p-2">
          <div className="flex space-x-2">
            <div className="w-2 h-2 bg-blue-600 rounded-full animate-bounce"></div>
            <div className="w-2 h-2 bg-blue-600 rounded-full animate-bounce" style={{ animationDelay: '0.2s' }}></div>
            <div className="w-2 h-2 bg-blue-600 rounded-full animate-bounce" style={{ animationDelay: '0.4s' }}></div>
          </div>
        </div>
      )}

      {/* Input Area */}
      <div className="border-t border-gray-200 bg-white p-4 shadow-lg">
        <div className="container mx-auto max-w-4xl">
          <div className="flex items-center">
            <input
              ref={inputRef}
              type="text"
              value={message}
              onChange={(e) => setMessage(e.target.value)}
              onKeyDown={handleKeyDown}
              className="flex-1 p-3 border border-gray-300 rounded-l-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              placeholder="Type your message here..."
              disabled={isLoading}
            />
            <button
              onClick={handleSubmit}
              className="bg-blue-600 hover:bg-blue-700 text-white p-3 rounded-r-lg transition-colors duration-300"
              disabled={isLoading || !message.trim()}
            >
              <Send size={20} />
            </button>
          </div>
          <div className="mt-2 text-xs text-gray-500 text-center">
            Ask about creating a ticket, checking status, or any other help you need.
          </div>
        </div>
      </div>
    </div>
  );
}
