import React, { useState, useEffect } from 'react';
import axios from 'axios';

const App: React.FC = () => {
  const [message, setMessage] = useState('');
  const [chatHistory, setChatHistory] = useState([]);
  const [userId] = useState('user123'); // Replace with dynamic user ID if needed
  const [ticketId, setTicketId] = useState('');

  useEffect(() => {
    // Fetch initial chat history if needed
  }, []);

  const handleSend = async () => {
    if (!message.trim()) return;

    const response = await axios.post('http://localhost:5000/chat', {
      message,
      user_id: userId,
    });
    setChatHistory((prev) => [...prev, { user: message, bot: response.data.message }]);
    setMessage('');
  };

  const handleReset = async () => {
    await axios.post('http://localhost:5000/reset');
    setChatHistory([]);
  };

  const handleCheckStatus = async () => {
    if (!ticketId) return;
    const response = await axios.post('http://localhost:5000/chat', {
      message: `check_status ${ticketId}`,
      user_id: userId,
    });
    setChatHistory((prev) => [...prev, { user: `check_status ${ticketId}`, bot: response.data.message }]);
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-100 p-4">
      <div className="w-full max-w-md bg-white rounded-lg shadow-lg p-6">
        <h1 className="text-2xl font-bold text-center text-gray-800 mb-4">Agentic Chat</h1>
        <div className="h-64 overflow-y-auto mb-4 border rounded p-2 bg-gray-50">
          {chatHistory.map((chat, index) => (
            <div key={index} className="mb-2">
              <span className="font-semibold text-blue-600">You:</span> {chat.user}
              <div className="text-gray-700 mt-1"><span className="font-semibold text-green-600">Bot:</span> {chat.bot}</div>
            </div>
          ))}
        </div>
        <input
          type="text"
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          className="w-full p-2 mb-2 border rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
          placeholder="Type your message..."
        />
        <button
          onClick={handleSend}
          className="w-full bg-blue-500 text-white p-2 rounded hover:bg-blue-600 transition"
        >
          Send
        </button>
        <input
          type="text"
          value={ticketId}
          onChange={(e) => setTicketId(e.target.value)}
          className="w-full p-2 mt-2 border rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
          placeholder="Enter Ticket ID to check status..."
        />
        <button
          onClick={handleCheckStatus}
          className="w-full bg-green-500 text-white p-2 mt-2 rounded hover:bg-green-600 transition"
        >
          Check Status
        </button>
        <button
          onClick={handleReset}
          className="w-full bg-red-500 text-white p-2 mt-2 rounded hover:bg-red-600 transition"
        >
          Reset Chat
        </button>
      </div>
    </div>
  );
};

export default App;
